import React from 'react';
import axios from 'axios';

class User extends React.Component{

  state={
      utilisateur: []
  }

  componentDidMount(){

    axios.get( 'https://jsonplaceholder.typicode.com/users/',{
    params: {
      id: this.props.match.params.id
    }
  } )
      .then(resultat => {
        console.log(resultat.data)
        this.setState({
          utilisateur: resultat.data
        })
      })
    }

  render(){
    const {utilisateur}=this.state;
    console.log(utilisateur)
    const userShow=utilisateur.map(use=>{
      return(
        <div>
          {use.name}
        </div>
      )
    })
  return(
    <div>
        hello
        {userShow}
        <p> le nom et prénom:{utilisateur.name}</p>
        <p>le surnom: {utilisateur.username}</p>
        <p>mail: {utilisateur.email}</p>
        <p>téléphone: {utilisateur.phone}</p>
        <p>ville: {utilisateur.city}</p>
        <p>site: {utilisateur.website}</p>

    </div>
  )}

}
export default User
