import React from 'react';
import axios from 'axios';

class User extends React.Component{
  state={
      utilisateur: []
  }
  componentDidMount(){

    axios.get( `https://jsonplaceholder.typicode.com/users/${props.match.params.id}`)
      .then(resultat => {

        this.setState({
          utilisateur: resultat.data
        })
      })
    }

  render(){
    const {utilisateur}=this.state;
  return(
    <div>
        hello
        <p> le nom et prénom:{utilisateur.name}</p>
        <p>le surnom: {utilisateur.username}</p>
        <p>mail: {utilisateur.email}</p>
        <p>téléphone: {utilisateur.phone}</p>
        <p>ville: {utilisateur.city}</p>
        <p>site: {utilisateur.website}</p>

    </div>
  )}

}
export default User
