import React from 'react';
import axios from 'axios';

class User extends React.Component{
  state={
      user: []
  }
  componentDidMount(){


    axios.get( 'https://jsonplaceholder.typicode.com/users/1')
    .then(res => {

      this.setState({
        user: res.data
      })
    })
  }
  render(){
    const {user}=this.state;
    console.log(user)
    const userListe=user.map(use=>{
    return (
      <div key={use.id}>
          <h4>utilisateur</h4>

          <p>{use.id} . le nom et prénom:{use.name}</p><p>le surnom: {use.username}</p><p>mail: {use.email}</p><p>téléphone: {use.phone}</p>
          <p>ville: {use.city}</p><p>site: {use.website}</p>
          <div>-----------------</div>
      </div>
    )})
  return(
    <div>
        hello
        {userListe}
    </div>
  )
  }
}
export default User
