import React, {Component} from 'react';
import axios from 'axios';

class Blog extends Component{
  state={
    users: []
  }
  componentDidMount(){
    axios.get("https://jsonplaceholder.typicode.com/users")
    .then(res => {
      console.log(res.data);
      this.setState({
        users: res.data
      })
    })
  }
  render(){
    return (
      <div>
          <h3>Blog</h3>
          <div>{this.state.users.map(user=><p>{user.name} et le surnom: {user.username}</p>)}</div>
      </div>
    )
  }
}
export default Blog;
